"use strict";

window.onload = () => {
  activeRadio();
  addOnchangeToRadio();
};

function activeRadio() {
  let questionsBody = document.querySelector('.quiz__question-body');
  let radioItems = questionsBody.querySelectorAll("input[type='radio']");
  radioItems.forEach(item => {
    if (item.checked) {
      item.parentElement.classList.add('question1__item_active');
    } else {
      item.parentElement.classList.remove('question1__item_active');
    }
  });
}

function addOnchangeToRadio() {
  let questionsBody = document.querySelector('.quiz__question-body');
  let radioItems = questionsBody.querySelectorAll("input[type='radio']");
  radioItems.forEach(item => {
    item.addEventListener('change', () => activeRadio());
  });
}
